import cv2 
import numpy as np
import urllib.request

#original image 1
urllib.request.urlretrieve('data:image/jpeg;base64,',"blue.png")
blue_green = cv2.imread('blue.png')
cv2.imshow('blue',blue_green)
cv2.waitKey()
cv2.destroyAllWindows()

#original image 2
urllib.request.urlretrieve('data:image/jpeg;base64,',"pink.png")
pink = cv2.imread('pink.png')
cv2.imshow('pink',pink)
cv2.waitKey()
cv2.destroyAllWindows()

blue_green[: , :115] = pink[: , :115]
cv2.imshow("swapped", blue_green)
cv2.waitKey()
cv2.destroyAllWindows()
